sys_script.script:
// Create a GlideNotification instance
var notification = new GlideNotification();

// Set the notification details
notification.setSubject("State Change Alert");
notification.setMessage("The state of record " + current.number + " has changed from " + previous.state + " to " + current.state);
notification.setPriority(GlideNotification.PRIORITY_NORMAL); // Set the priority as needed

// Add recipients - You can specify users, groups, or roles
notification.addRecipient(current.assigned_to); // Notify the assigned user

// Send the notification
notification.send();
